package recipe.project.app;

import recipe.project.list.LinkedList;

import static recipe.project.utilities.StringUtils.join;
import static recipe.project.utilities.StringUtils.split;
import static recipe.project.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
